// eslint-disable-next-line 
import React, { useState } from "react";
import { Button, Box, Grid, TextField } from "@mui/material";
import proto from '../pb/proto_grpc_web_pb';

var loginService = new proto.LoginServiceClient('http://0.0.0.0:8000');

export default function Login (){
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const [error_message, setErrorMessage] = useState();
    const [token, setToken] = useState();

    const handleSubmit = async e => {
        e.preventDefault();

        const handleSubmit = () => {
            if (email !== "" && password !== ""){
                let request = new proto.LoginRequest();
                request.setEmail(email);
                request.setPassword(password);
                loginService.login(request, {}, (error, response) => { 
                    if(error !== undefined){
                        setErrorMessage(response.getError())
                        return ;
                    }

                    setToken(response.getToken())
                })
            }
        }
    }

    return (
        <Grid
            container
            spacing={2}
            direction="column"
            alignItems="center"
            justifyContent="center"
            style={{ minHeight: '100vh' }}
            >

            <Grid item xs={3}>
                <Box 
                    component="form"
                    onSubmit={handleSubmit}

                >
                    {error_message !== '' &&
                    <Grid item xs={12}> 
                        <Alert severity="error">{ error_message }</Alert>  
                    </Grid>}

                    <TextField
                        label="Email"
                        id="email"
                        fullWidth
                        onChange={e => setEmail(e.target.value)}
                        type="text"

                        sx={{ mt: 3 }}
                    />
                    <TextField
                        label="Password"
                        id="password"
                        fullWidth
                        onChange={e => setPassword(e.target.value)}
                        type="password"

                        sx={{ mt: 3 }}

                    />

                    <Button 
                        type="submit"
                        color="primary"                         
                        className="form__custom-button"
                        fullWidth
                        sx={{ mt: 3 }}
                        >
                        Log in
                    </Button>
                </Box>
            </Grid>       
        </Grid> 
    );
}
